import 'package:auto_size_text/auto_size_text.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_smart_dialog/flutter_smart_dialog.dart';class GeoLocationPermissionWidget extends StatelessWidget {  const GeoLocationPermissionWidget({super.key});  @override  Widget build(BuildContext context) {    return SizedBox(      height: MediaQuery.of(context).size.height*0.65,      width: MediaQuery.of(context).size.width*0.8,      child: Padding(        padding: EdgeInsets.all(10),        child: SingleChildScrollView(          child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              SizedBox(height: 20,),              Center(child: Icon(CupertinoIcons.location_solid,size: 40,color: Colors.green ,),),              SizedBox(height: 10,),              Center(child: Text("Find best places around you",textScaleFactor: 1.4,),),              SizedBox(height: 10,),              Image.asset(                "assets/images/geo.jpg",                width: double.infinity,              ),              SizedBox(height: 10,),              Text("Please allow TimeCept to access your location to find restaurants,cinemas,shopping malls near you"),              SizedBox(height: 20,),              SizedBox(                width: double.infinity,                child: OutlinedButton.icon(                  icon: Icon(Icons.location_on_outlined),                  label: AutoSizeText("Allow location services",maxLines: 1,),                  onPressed: (){                    SmartDialog.dismiss(result: true);                  },                ),              ),              SizedBox(                width: double.infinity,                child: OutlinedButton(onPressed: (){SmartDialog.dismiss(result: false);}, child: AutoSizeText("Not now")),              )            ],          ),        ),      ),    );  }}